package com.version3;

import java.util.Scanner;

public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	   for(int i=0;i<=10;i++) {
		   System.out.println("Hello World!"+i);
		   
	   }
	   System.out.println("=========");
	   for(int i=10;i>=1;i--) {
		   System.out.println("Hello World!"+i);
		

	}

	}
}
