package com.version1;

public class UnayExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=20;
		
		System.out.println("\t"+(x++));//Post Increment
		System.out.println("\t"+(++y));//Pre Increment
		System.out.println("\t"+(x));
        System.out.println("   ===============  ");
		int a=10;
		int b=20;
		System.out.println("\t"+(a--));//post decrement
		System.out.println("\t"+(--b));//pre  decrement
		System.out.println("\t"+(a));
	}

}
